mapper.select:

  
     and template_id = #{templateId}
     and detect_title = #{detectTitle}
     and type = #{type}
     and unit_type = #{unitType}
     and module_type = #{moduleType}
     and detect_module = #{detectModule}
     and parent_id = #{parentId}
     and first_code = #{firstCode}
     and first_content = #{firstContent}
     and secondary_code = #{secondaryCode}
     and secondary_content = #{secondaryContent}
     and weights = #{weights}
     and output = #{output}
     and max_score = #{maxScore}
     and view = #{view}
     and view_parent_id = #{viewParentId}

order by weights asc, first_code asc, id asc

select a.id, b.template_id, b.detect_title, b.type, b.unit_type, b.module_type, b.detect_module, b.parent_id, a.first_code, a.first_content, 
b.secondary_code, b.secondary_content, b.weights, b.output, b.max_score, b.view, a.view_parent_id, b.create_by, b.create_time, b.update_by, b.update_time 
from intuitive_detect_data a left join intuitive_detect_data b on a.view_parent_id = b.id
  
     and b.template_id = #{templateId}
     and b.detect_title = #{detectTitle}
     and b.type = #{type}
     and b.unit_type = #{unitType}
     and b.module_type = #{moduleType}
     and b.detect_module = #{detectModule}
     and b.parent_id = #{parentId}
     and b.first_code = #{firstCode}
     and b.first_content = #{firstContent}
     and b.secondary_code = #{secondaryCode}
     and b.secondary_content = #{secondaryContent}
     and b.weights = #{weights}
     and b.output = #{output}
     and b.max_score = #{maxScore}
     and a.view = #{view}
     and a.view_parent_id = #{viewParentId} and a.view = '2' 
     and a.view_parent_id is not null and a.view = '2' 

order by a.first_code asc, a.id asc

select a.id, b.template_id, b.detect_title, b.type, b.unit_type, b.module_type, b.detect_module, b.parent_id, a.first_code, a.first_content, 
b.secondary_code, b.secondary_content, b.weights, b.output, b.max_score, b.view, a.view_parent_id, b.create_by, b.create_time, b.update_by, b.update_time 
from intuitive_detect_data a left join intuitive_detect_data b on a.view_parent_id = b.id where a.id = #{id}

select id,first_content as name from intuitive_detect_data
  
     and template_id = #{templateId}
     and detect_title = #{detectTitle}
     and type = #{type}
     and unit_type = #{unitType}
     and module_type = #{moduleType}
     and detect_module = #{detectModule}
     and parent_id = #{parentId}
     and first_code = #{firstCode}
     and first_content = #{firstContent}
     and secondary_code = #{secondaryCode}
     and secondary_content = #{secondaryContent}
     and weights = #{weights}
     and output = #{output}
     and max_score = #{maxScore}
     and view = #{view}
     and view_parent_id = #{viewParentId}

mapper.update:
update intuitive_detect_data
template_id = #{templateId}, detect_title = #{detectTitle}, type = #{type}, unit_type = #{unitType}, module_type = #{moduleType}, detect_module = #{detectModule}, parent_id = #{parentId}, first_code = #{firstCode}, first_content = #{firstContent}, secondary_code = #{secondaryCode}, secondary_content = #{secondaryContent}, weights = #{weights}, weights_c_es = #{weightsCEs}, weights_c_nes = #{weightsCNes}, weights_d_es = #{weightsDEs}, weights_d_nes = #{weightsDNes}, attribution = #{attribution,typeHandler=com.ruoyi.electrical.type.ListStringTypeHandler}, output = #{output}, max_score = #{maxScore}, view = #{view}, view_parent_id = #{viewParentId}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime},
where id = #{id}
